mapper.select:
select

from reply
where commentid = #{commentid,jdbcType=INTEGER}
group by replytime
LIMIT #{start},#{offset}

mapper.insert:
insert into reply (replyid, commentid, replyinfo, 
  preplyid, replytime, freplyid
  )
values (#{replyid,jdbcType=INTEGER}, #{commentid,jdbcType=INTEGER}, #{replyinfo,jdbcType=VARCHAR}, 
  #{preplyid,jdbcType=INTEGER}, #{replytime,jdbcType=TIMESTAMP}, #{freplyid,jdbcType=INTEGER}
  )
